$_MODULE['<{stripe_official}prestashop>faq_a1a9b050eb8c5f99abe7b082ae7c3b19'] = 'Sowohl ApplePay als auch GooglePay benötigen eine sichere https, um angezeigt und genutzt werden zu können.';
$_MODULE['<{stripe_official}prestashop>secure_87e9a4dbf49420d3cf66572e68064f3d'] = '3D Secure';
$_MODULE['<{stripe_official}prestashop>secure_511237c4801d1d63a15a704e9b58f51c'] = 'Bei Visa- und MasterCard-Zahlungen können Sie 3D-Secure-Authentifizierung durchführen und damit für zusätzlichen Schutz sorgen.';
$_MODULE['<{stripe_official}prestashop>secure_9772536a89afaad6ebb31ff63a617f81'] = '3D Secure (verifiziert von VISA, MasterCard SecureCode™) ist ein System, mit dem die Identität eines Kunden vor Kaufabschluss verifiziert wird; es hilft Händlern dabei, die Betrugsraten zu senken.';
$_MODULE['<{stripe_official}prestashop>secure_c7b219645f1fc0db5623237c149c6e2b'] = 'Mit 3D-Secure werden Kunden zu einer von ihrer Bank bereitgestellten Seite umgeleitet, auf der sie dazu aufgefordert werden, ein zusätzliches Passwort einzugeben, damit ihre Karte belastet werden kann.';
$_MODULE['<{stripe_official}prestashop>secure_1bb1625040ded5523a6234287b0c48f3'] = 'Auf unserer Website finden Sie weitere Informationen zu 3D-Secure:';
$_MODULE['<{stripe_official}prestashop>started_4de95da2c7e543ec90b016394d4b3b83'] = 'Verbessern Sie Ihren Umrechnungskurs und akzeptieren Sie Zahlungen Ihrer Kunden mit Stripe, der unkompliziertesten Zahlungsplattform.';
$_MODULE['<{stripe_official}prestashop>started_c1be5a64d3f82fc89e8d97b4864892a2'] = 'Erstellen Sie Ihr Stripe-Konto innerhalb von 10 Minuten und beginnen Sie sofort mit der Annahme von Zahlungen über Visa, MasterCard und American Express (kein zusätzlicher Vertrag oder Händler-ID von Ihrer Bank erforderlich)';
$_MODULE['<{stripe_official}prestashop>started_863744a8875e8ea9d6623118140894c6'] = 'et acceptez immédiatement les paiements par Visa, MasterCard et American Express (aucun contrat VAD à ouvrir auprès de votre banque)';
$_MODULE['<{stripe_official}prestashop>started_ebd8fbb49032e4992eb5050893dfa1fc'] = 'Sich mit Stripe verbinden';
$_MODULE['<{stripe_official}prestashop>started_9bbf07a0144267df0eaf265f4040495c'] = 'Verbessern Sie Ihren Umrechnungskurs, indem Sie Ihren Kunden ein nahtloses Zahlungserlebnis bieten: Mit Stripe können Sie das Zahlungsformular auf Ihren eigenen Seiten hosten, ohne auf eine Bank-Drittseite umleiten zu müssen.';
$_MODULE['<{stripe_official}prestashop>started_986f6dc9620dad1247fedaf8c66054c6'] = 'en offrant une expérience de paiement optimale à vos clients. Stripe vous permet d\'héberger les formulaires de paiement sur vos propres pages, en toute sécurité, sans redirection vers une page bancaire.';
$_MODULE['<{stripe_official}prestashop>started_343084bfe89ba2c466ec560c17e0559f'] = 'Senken Sie das Betrugsrisiko mithilfe unserer Anti-Betrugs-Tools 3D-Secure und Stripe Radar. ';
$_MODULE['<{stripe_official}prestashop>started_47799f57c433224fa5bbd080d071271d'] = 'grâce au 3D-Secure paramétrable et à';
$_MODULE['<{stripe_official}prestashop>started_76bdc5af356869dc7b70d186ad67ce50'] = 'Stripe Radar';
$_MODULE['<{stripe_official}prestashop>started_e90c7e7a12ff8db56945e2917dbe8f6b'] = ', notre suite d\'outils anti-fraude.';
$_MODULE['<{stripe_official}prestashop>started_45f33ed53cebf4345835becb19d47614'] = 'Einfache Rückerstattung Ihrer Bestellungen über das Backoffice Ihres PrestaShop (und automatische Aktualisierung Ihres PrestaShop-Bestellstatus).';
$_MODULE['<{stripe_official}prestashop>started_969701241cd7ad8038c71c1f6d1f800e'] = 'vos commandez depuis votre back-office PresatShop (avec une actualisation automatique du statut de votre commande sur votre back-office).';
$_MODULE['<{stripe_official}prestashop>started_46df934279f25fbda7d710d1820a2df8'] = 'Beginnen Sie, im Ausland zu verkaufen, indem Sie Zahlungen in über 135 Währungen und 4 lokalen Zahlungsmethoden (iDEAL, Bancontact, SOFORT, Giropay) anbieten.';
$_MODULE['<{stripe_official}prestashop>started_f3036bf0187d521942fd61676c7b7def'] = 'Plus de 135 devises';
$_MODULE['<{stripe_official}prestashop>started_14b9931efb141b3733e1f4922d7ff728'] = 'et 4 moyen de paiement locaux (iDEAL, Bancontact, SOFORT, Giropay).';
$_MODULE['<{stripe_official}prestashop>started_366cf9d9166ad7cc076c8e82587854df'] = 'Weitere Informationen zu Stripe finden Sie auf unserer Website:';
