$_MODULE['<{stripe_official}prestashop>faq_6e90de9c82f8d2d2906b5365651d8640'] = 'Para mayor información: [a @href1@]https://stripe.com/docs/keys[/a] y condiciones de uso en [a @href2@]https://stripe.com/docs/testing[/a].';
$_MODULE['<{stripe_official}prestashop>faq_f268b9c7a24de3ce8f09dc235fe108be'] = '¿Cómo puedo comprobar si mi instalación se ha realizado correctamente?';
