$_MODULE['<{stripe_official}prestashop>faq_6017e92de7da495238eda556788855bf'] = 'U kunt onze testkaartnummers gebruiken: [a @href1@]https://stripe.com/docs/testing[/a].';
$_MODULE['<{stripe_official}prestashop>faq_7a19ad25bb8d4edad16120a157a9c572'] = 'Als de module goed werkt in de testmodus, moet deze in de livemodus in principe ook goed werken.';
